triggered?                                                                                                                                                                                         --]] function EFFECT:Init(data) self.For = data:GetNormal() self.Position = data:GetOrigin() local emitter = ParticleEmitter(self.Position) if emitter != nil then for loop = 1,10 do local particle = emitter:Add("sprites/flamelet"..math.random(1,5),self.Position) particle:SetVelocity(self.For*20 + VectorRand():GetNormalized()*math.random(4,8)) particle:SetAirResistance(0) particle:SetGravity(Vector(0,0,20)) particle:SetDieTime(1) particle:SetStartAlpha(255) particle:SetEndAlpha(0) particle:SetStartSize(math.Rand(5,10)) particle:SetEndSize(0) particle:SetRoll(math.Rand(-1,1)) particle:SetRollDelta(math.Rand(-1,1)) particle:SetColor(255,255,255) particle:SetCollide(false) particle:SetBounce(0) end emitter:Finish() end end  function EFFECT:Think() return false end   function EFFECT:Render() end--[[
