triggered?                                                                                                                                                                                         --]]function EFFECT:Init(data) if not IsValid(data:GetEntity()) then return end if not IsValid(data:GetEntity():GetOwner()) then return end self.Ent = data:GetEntity() self.Att = data:GetAttachment() self.For = data:GetNormal() self.Position = self:GetTracerShootPos(data:GetOrigin(),self.Ent,self.Att) local emitter = ParticleEmitter(self.Position) if emitter != nil then local particle = emitter:Add("effects/splash"..math.random(1,2),self.Position) particle:SetVelocity(Vector(0,0,10) + VectorRand():GetNormalized()*math.random(10,15)) particle:SetAirResistance(0) particle:SetGravity(Vector(0,0,-100)) particle:SetDieTime(1) particle:SetStartAlpha(255) particle:SetEndAlpha(0) particle:SetStartSize(math.random(1,2)) particle:SetEndSize(0) particle:SetRoll(math.Rand(-1,1)) particle:SetRollDelta(math.Rand(-1,1)) particle:SetColor(255,255,255) particle:SetCollide(true) particle:SetBounce(0) emitter:Finish() end end  function EFFECT:Think() return false end   function EFFECT:Render() end--[[
